import eu.bcvsolutions.idm.core.notification.api.dto.IdmMessageDto;
import eu.bcvsolutions.idm.core.CoreModuleDescriptor;
import eu.bcvsolutions.idm.core.notification.api.domain.NotificationLevel;
import eu.bcvsolutions.idm.core.api.domain.ConceptRoleRequestOperation;
import java.time.format.DateTimeFormatter;

// get request ID from event
def requestId = entityEvent.content.id;

// transform date
DateTimeFormatter dateFormat = DateTimeFormatter.ofPattern("dd. MM. yyyy HH:mm");
def from = dateFormat.format(entityEvent.content.created);

// gather all information about roles for send notification

def addedRoles = conceptManager.getAllByRoleRequest(requestId, ConceptRoleRequestOperation.ADD);
def changedRoles = conceptManager.getAllByRoleRequest(requestId, ConceptRoleRequestOperation.UPDATE);
      def removedRoles = conceptManager.getAllByRoleRequest(requestId, ConceptRoleRequestOperation.REMOVE);

def sendNotificationToApplicant = configurationService.getBooleanValue("idm.sec.core.wf.notification.applicant.enabled", true);
def sendNotificationToImplementer = configurationService.getBooleanValue("idm.sec.core.wf.notification.implementer.enabled", false);

def applicantIdentity = roleRequestService.getApplicantAsIdentity(entityEvent.content);

def implementerIdentity = identityService.get(implementerIdentifier);

def url = getUrl(entityEvent.content);

//send notification
if (applicantIdentity != null && implementerIdentity.getId().equals(applicantIdentity.getId())) {
  if(sendNotificationToImplementer || sendNotificationToApplicant){
    notificationManager.send(
     CoreModuleDescriptor.TOPIC_CHANGE_IDENTITY_ROLES_IMPLEMENTER,
              new IdmMessageDto.Builder()
       .setLevel(NotificationLevel.SUCCESS)
       .addParameter("addedRoles", addedRoles)
       .addParameter("changedRoles", changedRoles)
       .addParameter("removedRoles", removedRoles)
       .addParameter("identity", applicantIdentity)
       .addParameter("from", from)
       .addParameter("url", url)
     .build(),
     applicantIdentity
    );
  }
} else {
if(sendNotificationToApplicant && applicantIdentity != null){
  notificationManager.send(
   CoreModuleDescriptor.TOPIC_CHANGE_IDENTITY_ROLES,
            new IdmMessageDto.Builder()
     .setLevel(NotificationLevel.SUCCESS)
     .addParameter("addedRoles", addedRoles)
     .addParameter("changedRoles", changedRoles)
     .addParameter("removedRoles", removedRoles)
     .addParameter("identity", applicantIdentity)
     .addParameter("from", from)
     .addParameter("url", url)
   .build(),
   applicantIdentity
  );
}
  if(sendNotificationToImplementer){
    notificationManager.send(
     CoreModuleDescriptor.TOPIC_CHANGE_IDENTITY_ROLES_IMPLEMENTER,
              new IdmMessageDto.Builder()
       .setLevel(NotificationLevel.SUCCESS)
       .addParameter("addedRoles", addedRoles)
       .addParameter("changedRoles", changedRoles)
       .addParameter("removedRoles", removedRoles)
       .addParameter("identity", applicantIdentity)
       .addParameter("from", from)
       .addParameter("url", url)
     .build(),
     implementerIdentity
    );
  }
}


def getUrl(request){
	if (request == null) {
		return null;
	}
	return configurationService.getFrontendUrl("/role-requests/"+request.getId()+"/detail");
}

