              .addParameter("from", from)
              .addParameter("applicantIdentity", applicantIdentity)
              .build(),
              applicantIdentity
              );
            }
          } else {
            if(sendNotificationToApplicant && applicantIdentity != null){
              notificationManager.send(
              CoreModuleDescriptor.TOPIC_DISAPPROVE_IDENTITY_ROLES,
