import eu.bcvsolutions.idm.core.api.dto.IdmRoleRequestDto;
import eu.bcvsolutions.idm.core.api.dto.ResolvedIncompatibleRoleDto;

// Check if exists some incompatibilities for that request
IdmRoleRequestDto requestDto = roleRequestService.get(entityEvent.content.id);
Set<ResolvedIncompatibleRoleDto> incompatibilities = roleRequestService.getIncompatibleRoles(requestDto);

//Save result to variables
execution.setVariable("existsIncompatibilities", !incompatibilities.isEmpty());
